import React from 'react'
import '../styles/style.css'
import Cart from './Cart'
import Navbar from './Navbar'
import ShoppingCart from './ShoppingCart'

export default function Product(props) {

    const [cartItems, setCartItems] = React.useState([])

    const [cartCount, setCartCount] = React.useState(0)

    const [shopCart, setShopCart] = React.useState([])
    // React.useEffect(() => {

    // }, [])


    function addToCart(item) {
        setCartItems(prevCartItems => [...prevCartItems, {
            id: item.id,
            image: item.image,
            title: item.title,
            price: item.price,
            count: cartCount
        }])
        setCartCount(cartCount+1)
        setShopCart(cartItems);
    }   

    console.log(cartItems, shopCart)

    const productEle = props.details.map(product => <div key={product.id} className='item-container'>
                                                        <img src={product.image} />
                                                        <h3>{product.title}</h3>
                                                        <p>â‚¹{product.price}</p>
                                                        <button onClick={() => addToCart(product)}>ADD TO CART</button>
                                                    </div>
                                                    )

    return (
        <div className='products'>
            {/* <ShoppingCart 
                cartItem={cartItems}
            /> */}
            {productEle}
        </div>
    )
}
