    //             <div className='checkout-selection-tool'>
    //                 <button>−</button>
    //                 <input type='number' min='1'/>
    //                 <button>+</button>
    //             </div>
    //             <h4>₹109.95</h4>
    //         </div>}

    return (
        <div className='checkout-cart'>
            <h2>Your Shopping Cart</h2>
            {cartItemsEle}
            <h3>Total: ₹0.00</h3>
            <button>CHECKOUT</button>
            <button>CLOSE</button>
        </div>
    )
}
